public class test{public static void main(String[]args){int r=Integer.parseInt(args[0]);int trials=Integer.parseInt(args[1]);int exp=trials;double x=0;double y=0;double totalsteps=0;int n=0;double direction=0;while(exp>0){x=0;y=0;n=0;while((Math.abs(x)+Math.abs(y))<r){direction=Math.random();if(direction<0.25){y++;}else if((direction>=0.25)&&(direction<0.50)){x++;}else if((direction>=0.50)&&(direction<0.75)){y--;}else{x--;}n++;}exp--;totalsteps+=n;}double average=totalsteps/trials;System.out.println("average number of steps = "+average);}}